mapper.insert:

  SELECT NVL(MAX(department_id),0)+1
  FROM	departments			

  insert into departments
  			(
  			department_id
        	,department_name
       		 ,manager_id
	       	 ,location_id
	       	 )
  values	(
  			#{departmentId}
  			,#{departmentName}
  			,#{managerId}
  			,#{locationId}
  			)

mapper.update:
update	departments
set		department_name = #{departmentName}
			,manager_id = #{managerId}
			,location_id = #{locationId}
where		department_id = #{departmentId}
